<template>
  <div class="loginpage" style="height: 100vh; overflow: hidden">
    <section id="login">
      <!-- <div class="container-fluid"> -->
      <div class="login-container">
        <div id="gambarOneId" :class="{ 'login-sub-container': true }">
          <div class="w-100" id="formInputLogin" style="padding: 0px">
            <figure id="logo" class="w-100 d-flex justify-content-center mb-3">
              <!-- <img src="asset/img/logo-gan.png" alt="" style="width:80px;"> -->
            </figure>
            <h2 class="text-center mb-5 mb-md-3" id="psiKotesttitle">
              Masuk Ke Psikotest
            </h2>
            <div :class="{ isVisible: isVisible }">
              <!-- toast -->
              <div class="wrapper mb-4">
                <div id="toast">
                  <div class="container-1 mr-3">
                    <img
                      width="50px"
                      height="50px"
                      src="../../public/asset/img/remove.png"
                    />
                  </div>
                  <div class="container-2">
                    <h4>User tidak ditemukan</h4>
                    closeToast
                    <p>Akun yang anda masukan salah, harap cek kembali.</p>
                  </div>
                  <!-- <button id="close" onclick="()">&times;</button> -->
                </div>
              </div>
            </div>
            <form @submit.prevent="login">
              <div class="mb-2">
                <label class="label" style="font-weight: 700">Email</label>
                <div class="field has-addons">
                  <div class="control is-expanded">
                    <input
                      placeholder="Masukkan Email"
                      type="text"
                      class="form-control"
                      v-model="email"
                    />
                    <!-- <input v-else type="password" class="form-control" v-model="password"> -->
                  </div>
                </div>
              </div>
              <div class="mb-4">
                <label class="label" style="font-weight: 700">Password</label>
                <div class="field has-addons">
                  <div class="control is-expanded">
                    <div class="custom-input">
                      <input
                        placeholder="password"
                        type="password"
                        class="form-control cstm-fc"
                        v-model="password"
                        id="password"
                      />
                      <div v-on:click="togglePassword">
                        <img
                          v-if="isHidden"
                          style="width: auto; height: 20px; padding-right: 10px"
                          src="../assets/eye.png"
                          alt="toggle"
                        />
                        <img
                          v-else
                          style="width: auto; height: 20px; padding-right: 10px"
                          src="../assets/hidden.png"
                          alt="toggle"
                        />
                      </div>

                      <!-- <input v-else type="password" class="form-control" v-model="password"> -->
                    </div>
                  </div>
                </div>
              </div>

              <label
                >Lupa Password?<router-link
                  to="/reset"
                  class="underliness"
                  id="rest-1"
                >
                  Reset Password</router-link
                ></label
              >
              <br />
              <button
                class="btn mpa-btn-primary w-100 text-white"
                id="btnSubmitDD"
              >
                Submit
              </button>
              <br />
            </form>
          </div>
        </div>
      </div>

      <!-- </div> -->
    </section>
  </div>
</template>

<script>
import axios from "axios";
import { useCookies } from "vue3-cookies";

export default {
  name: "LoginComponent",
  setup() {
    const { cookies } = useCookies();
    return {
      cookies,
    };
  },
  data() {
    return {
      email: [],
      password: null,
      showPassword: false,
      isHidden: false,
      isVisible: true,
    };
  },
  mounted() {
    this.checkToken();
  },
  computed: {
    buttonLabel() {
      return this.showPassword ? "Hide" : "Show";
    },
  },
  methods: {
    async login() {
      try {
        var data = await axios.post(
          "/login",
          {
            email: this.email,
            password: this.password,
          },
          {
            headers: {
              destination: "Auth",
            },
          }
        );
        // console.log(data);
        this.cookies.set("name", data.data.data.name);
        this.cookies.set("origin", data.data.data.origin);
        this.cookies.set("X-GAN-TOKEN", data.data.data.token);
        this.$router.push("/dashboard");
      } catch (error) {
        // window.alert("Akun Tidak Ditemukan");
        this.isVisible = false;
        this.email = "";
        this.password = "";
        setTimeout(() => {
          this.isVisible = true;
        }, 4000);
      }
    },
    togglePassword() {
      var x = document.getElementById("password");
      if (x.type === "password") {
        x.type = "text";
        this.isHidden = true;
      } else {
        x.type = "password";
        this.isHidden = false;
      }
    },
    checkToken() {
      if (this.cookies.get("X-GAN-TOKEN")) {
        this.$router.push("/dashboard");
      }
    },
  },
};
</script>

